import unittest
from activity.activity_SetPublicationStatus import activity_SetPublicationStatus
import settings_mock
from mock import mock, patch
from ddt import ddt, data

@ddt
class MyTestCase(unittest.TestCase):


    def setUp(self):
        self.setpublicationstatus = activity_SetPublicationStatus(settings_mock, None, None, None, None)

    @data('elife-00353-vor-v1.zip', 'elife-00353-vor-20121213000000.zip', 'elife-00353-vor-v1.zip')
    @patch.object(activity_SetPublicationStatus, 'load_settings')
    def test_get_publication_status_publish_true_default(self, filename, mock_load_settings):
        mock_load_settings.return_value = {'default': True, 'override': {'hold': {'doi': ['01$']}, 'publish': {'doi': ['353$']}}}
        publication_status = self.setpublicationstatus.get_publication_status(self.data_for_publication_status(), filename)
        self.assertEqual(publication_status, True)

    @data('elife-00353-vor-v1.zip', 'elife-00353-vor-20121213000000.zip', 'elife-00353-vor-v1.zip', 'elife-15524-vor-20121213000000.zip')
    @patch.object(activity_SetPublicationStatus, 'load_settings')
    def test_get_publication_status_publish_false_doi_does_not_match(self, filename, mock_load_settings):
        mock_load_settings.return_value = {'default': False, 'override': {'hold': {'doi': ['01$']}, 'publish': {'doi': ['353$']}}}
        publication_status = self.setpublicationstatus.get_publication_status(self.data_for_publication_status(), filename)
        self.assertEqual(publication_status, False)

    @data('elife-00353-vor-v1.zip', 'elife-00353-vor-20121213000000.zip', 'elife-00353-vor-v1.zip', 'elife-15524-vor-20121213000000.zip', 'elife-15524-vor-v1-20121213.zip')
    @patch.object(activity_SetPublicationStatus, 'load_settings')
    def test_get_publication_status_publish_false_doi_does_match(self, filename, mock_load_settings):
        mock_load_settings.return_value = {'default': False, 'override': {'hold': {'doi': ['0000000000001$']}, 'publish': {'doi': ['15224$']}}}
        publication_status = self.setpublicationstatus.get_publication_status(self.data_for_publication_status(), filename)
        self.assertEqual(publication_status, True)

    @data('.')
    @patch.object(activity_SetPublicationStatus, 'load_settings')
    def test_get_publication_status_publish_false_hold_doi(self, filename, mock_load_settings):
        mock_load_settings.return_value = {'default': True, 'override': {'hold': {'doi': ['15224$']}, 'publish': {'doi': ['000000000000$']}}}
        publication_status = self.setpublicationstatus.get_publication_status(self.data_for_publication_status(), filename)
        self.assertEqual(publication_status, False)

    @data('elife-00353-vor-v1-20121213000000.zip', 'elife-15524-vor-v1-20121213000000.zip')
    def test_get_get_publication_status_publish_true_file_update_date_and_version_presents(self, filename):
        publication_status = self.setpublicationstatus.get_publication_status(self.data_for_publication_status(), filename)
        self.assertEqual(publication_status, True)

    def test_file_of_published_article_true(self):
        publish = self.setpublicationstatus.file_of_published_article('00353/0000-0000/elife-00353-vor-v1-20121213000000.zip')
        self.assertEqual(publish, True)

    def test_file_of_published_article_false(self):
        publish = self.setpublicationstatus.file_of_published_article('00353/0000-0000/elife-00353-vor-r1.zip')
        self.assertEqual(publish, False)

